    );
  }
}

class Register extends React.Component {
  state = {
    name: "",
    password: "",
    email: "",
    auth: null
  };

  handleChangeName = event => {
    this.setState({ name: event.target.value });
  };

  handleChangePass = event => {
    this.setState({ password: event.target.value });
  };

  handleChangeEmail = event => {
    this.setState({ email: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();

    const user = {
      name: this.state.name,
      password: this.state.password,
      email: this.state.email
    };

    axios
      .post("http://localhost:3001/users/register", user)
      .then(res => {
        this.setState({ auth: res.data.registed });
      })
      .catch(err => {
        console.log("Noooooooo ");
      });
  };

  render() {
    if (this.state.auth) {
      return <Redirect to="/login/" />;
    }
    return (
      <div className="register-form">
        <div className="present">
          <p>
            Bienvenido a la versión Beta de Yu-Gi-Masters. Buscamos que los
            usuarios puedan encontrar información sobre mazos, cartas <br></br>{" "}
            y apoyo de la comunidad.
          </p>
        </div>
        <a href="/login/">¿Ya tienes cuenta? ¡Ingresa aquí!</a>
        <form onSubmit={this.handleSubmit} className="rf">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            onChange={this.handleChangeName}
          ></input>
          <label htmlFor="password">Password: </label>
          <input
            type="password"
            name="password"
            onChange={this.handleChangePass}
          ></input>
          <label htmlFor="email">Email: </label>
          <input
            type="text"
            name="email"
            onChange={this.handleChangeEmail}
          ></input>
          <button type="submit">Registrarse</button>
        </form>
      </div>
    );
  }
}

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      password: "",
      auth: null
    };
  }

  handleChangeName = event => {
    this.setState({ name: event.target.value });
  };

  handleChangePass = event => {
    this.setState({ password: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();

    const logger = {
      name: this.state.name,
      password: this.state.password
    };

    axios
      .post("http://localhost:3001/users/log", logger)
      .then(res => {
        console.log(res);
        this.setState({ auth: res.data.auth });
      })
      .catch(err => {
        console.log("NO SE HA INICIADO SESIÓN");
      });
  };

  render() {
    if (this.state.auth) {
      return <Redirect to="/homepage" />;
    }

    return (
      <div className="LoginForm">
        <form method="POST">
          <label htmlFor="name"> Usuario: </label>
          <input
            type="text"
            name="name"
            onChange={this.handleChangeName}
          ></input>
          <br></br>
          <br></br>
          <label htmlFor="password">Contraseña: </label>
          <input
            type="password"
            name="password"
            onChange={this.handleChangePass}
          ></input>
          <br></br>
          <br></br>
          <button type="button" onClick={this.handleSubmit}>
            ¡Ingresar!
          </button>
          <button type="button"> ¿Registrarse?</button>
        </form>
        <a href="/">Regresar</a>
      </div>
    );
  }
}

ReactDOM.render(<Welcome />, document.getElementById("root"));

