class Foros_Dispobles extends React.Component {
    render() {
        return (
            <section>
                <h3>Foros de la comunidad</h3>
                <input type="text" placeholder="Filtrar..." name="buscar" />
                <button className="submit"> Buscar </button>
                <ul>
                    <li>Foro 1</li>
