}

class Botones_De_Creacion extends React.Component {
    render() {
        return (
            <div>
                <button className="submit"> Crear Foro </button>
                <br></br> <br></br>
                <button className="submit"> Crear Mazo</button>
            </div>
        )
    }
}

class HomePage extends React.Component {
    render() {
        return (
            <div>
                <Nav />
