import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './css/index.css'
import './css/register.css'
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';


class Navo extends React.Component {
    render(){
        return(
            <header>
            <div className="logoType">
            <img
              src="https://carlisletheacarlisletheatre.org/images/yugioh-logo-transparent-5.png"
              alt="Logo"
              className="logo"
            ></img>
          </div>
            </header>
        )
    }
}
//Barra de navegacion principal de la pagina
class Nav extends React.Component {
    render() {
      return (
        <header>
          <div className="logoType">
            <img
              src="https://carlisletheacarlisletheatre.org/images/yugioh-logo-transparent-5.png"
              alt="Logo"
              className="logo"
            ></img>
          </div>
          <nav className="menuNav">
            <ul>
              <li>
                <span>Home</span>
              </li>
              <li>
                <span>Perfil</span>
              </li>
              <li>
                <span>Amigos</span>
              </li>
            </ul>
          </nav>
        </header>
      );
    }
  }
//Footer de la pagina, informacion de redes sociales, y de contacto
class Footer extends React.Component {
    render() {
      return (
        <footer className="footer">
          <ul>
            <li>
            <img
              src="https://seeklogo.com/images/I/instagram-new-2016-logo-4773FE3F99-seeklogo.com.png"
              alt="face"
              className="facebook"
            ></img>
            </li>
            <li className="info">
              <span><a href='/info/'>Acerca de</a></span>
            </li>
            <li>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/5/51/Facebook_f_logo_%282019%29.svg"
              alt="insta"
              className="instagram"
            ></img>
            </li>
          </ul>
        </footer>
      );
    }
  }

  //Enlace de informacion en el footer de la pagina
  class Informacion extends React.Component{
      render(){
          return(
            <div>
                <p>
                    Yu-Gi-Masters nació por la nececidad de la creciente comunidad, de El Salvador, de jugadores del popular juego de cartas, Yu-Gi-Oh!,
                    de compartir entre si, de manera más amigable, sus inquietudes sobre el juego, torneos locales, estrategias, encontrar jugadores 
                    cercanos con quienes interactuar, competir, y aprender. <br><br></br></br>

                    Ya que la comunidad esta llena de gente muy experimentada, de nivel medio, y novatos, Yu-Gi-Web es el lugar perfecto
                    para que todo tipo de jugador pueda aprender de los más veteranos, foguearse con gente de nivel medio, y conocer jugadores 
                    recientes.<br></br>

                    Nuevo fundador, Mario Rene Villalobos Mejía, tiene la visión de  
                </p>
            </div>
          );
      }
  }

//Ventana para la creacion de publicacion dentro de un foro

//Formulario para la creacion de la publicacion
class PostCreation extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        autor: " ",
        title: " ",
        text: " "
      };
    }

    render() {
      return (
        <div className="body">
          <section className="bodySection">
            <br></br>
            <div className="span">
            <span className="frase">
              Crea un post para interactuar con la comunidad
            </span>
            </div>
            <div className="article">
            <form>
              <h3>Titulo</h3>
              <div className="title">
              <input
              className="boxTitle"
                type="text"
                placeholder="Un título creativo..."
                value={this.state.title}
                onChange={this.setTitle.bind(this)}
              ></input>
              </div>
              <br></br> <br></br>
              <h3 className="postCont">Contenido</h3>
              <div className="content">
              <textarea
                className="BigText"
                type="text2"
                placeholder="Discusion"
                value={this.state.text}
                onChange={this.setText.bind(this)}
              /></div>
              <div className="submitBtn">
              <button> Publicar </button>
              </div>
            </form>
            </div>
          </section>
        </div>
      );
    }

    setTitle(event) {
      this.setState({
        title: event.target.value
      });
    }

    setText(event) {
      this.setState({
        text: event.target.value
      });
    }
  }

//Vetana de creacion

class Create_Post_Window extends React.Component {
    render() {
        return (
            <div>
                <Nav />
                <PostCreation />
                <Footer />
            </div>
        )
    }
}

//Ventana para crear los foros del usuario

class CreateForo extends React.Component {
    state = {
        nombre: "",
        id_usuario: "",
        numero_miembros: "",
        miembros: "",
        publicaciones: "",
        fecha_creacion: ""
    }

    render() {
        return (
            <section>
                <span>Crea un foro a tu gusto</span>
                <article>
                    <h3>Nombre de tu foro</h3>
                    <form onSubmit={this.handleForum}>
                        <input type="text" placeholder="Nombre creativo..." name="nombre" onChange={this.AgregarNombre.bind(this)} />
                        <br></br> <br></br>
                        <button className="submit"> Crear </button>
                    </form>
                </article>
            </section>
        )
    }

    //Cambia el estado del atributo de nombre que tendra el foro

    AgregarNombre(event) {
        this.setState({
            nombre: event.target.value
        })
    }
}

//Componente de creacion de foro

class CreateForumWindow extends React.Component {
