                </article>
            </section>
        )
    }

    //Cambia el estado del atributo de nombre que tendra el foro

    AgregarNombre(event) {
        this.setState({
            nombre: event.target.value
        })
    }
}

//Componente de creacion de foro

class CreateForumWindow extends React.Component {
